import React, { useState } from "react"
import './App.css';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import {Login} from './pages/login_register/Login';
import {Register} from './pages/login_register/Register';
import {ShoppingList} from './pages/app/ShoppingList';
import { Sidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import axios from 'axios';

function App() {

  const [loginRegisterForm, setLoginRegisterForm] = useState('loginPage')
  const [loggedIn, setLoggedIn] = useState(false);

  const toggleForm = (formName) => {
    setLoginRegisterForm(formName);
  }

  const handleLogin = async (emailuser, password) => {
    try {
      if (emailuser === 'nish') {
        setLoggedIn(true);
      } else {
        setLoggedIn(false);
      }
    } catch (error) {
    }
  };

  /*const handleLogin = async (emailuser, password) => {
    try {
      // Call your login API function with the provided credentials
      const response = await apiLogin(credentials);
      // Assuming the server responds with a success status
      if (response.status === 'success') {
        setLoggedIn(true); // Update the logged-in state
      } else {
        // Handle unsuccessful login, e.g., display an error message
      }
    } catch (error) {
      // Handle API call errors
    }
  };
  */

  if (loggedIn) {
    return (
      <Router>
      <div className="App">
      <Sidebar>
        <Menu>
          <MenuItem component={<Link to="/ShoppingList" />}> Documentation</MenuItem>
          <MenuItem component={<Link to="/ShoppingList" />}> Calendar</MenuItem>
          <MenuItem component={<Link to="/ShoppingList" />}> E-commerce</MenuItem>
        </Menu>
      </Sidebar>;
      <Route path="/ShoppingList" component={ShoppingList} />
      </div>
      </Router>
    );
  }

  return (
    <div className="App">
      {
        loginRegisterForm === 'loginPage'? <Login onFormChange={toggleForm} onLogin={handleLogin}/> : <Register onFormChange={toggleForm} />
      }
    </div>
  );
}

export default App;
