import React, { useState } from 'react';
import SelectQuantity from './SelectQuantity';

function ShoppingRow({ item, onDelete }) {
    const [myitem, setItem] = useState(item.name);
    const [quantity, setQuantity] = useState(item.quantity);

    const handleItemChange = (e) => {
        setItem(e.target.value);
    };

    const handleQuantityChange = (e) => {
        setQuantity(e.target.value);
    };

    const handleDelete = () => {
        // Call the onDelete callback with the item or identifier to delete the row
        onDelete(item);
    };

    return (
        <tr className='itemrow'>
            <td>
                <input type="text" value={myitem} onChange={handleItemChange} />
            </td>
            <td>
                <input type="text" value={quantity} onChange={handleQuantityChange} />
            </td>
            <td>
                <button onClick={handleDelete}>Delete</button>
            </td>
        </tr>
    );
}

export default ShoppingRow;
