import React, { useState } from 'react';
import ShoppingRow from './ShoppingRow';
import { useNavigate } from 'react-router-dom';
import { SelectDrop } from './Select';

function Import() {
  const [allowFileSelection, setAllowFileSelection] = useState(false);

  const handleBrowse = () => {
    const shouldAllowSelection = window.confirm('Are you sure you want to select a file?');
    setAllowFileSelection(shouldAllowSelection);
  };

  return (
    <div>
      <h3>Show a file-select field which allows only one file to be chosen:</h3>
      <form action="/action_page.php">
        <label htmlFor="myfile">Select a file:</label>
        <input type="file" id="myfile" name="myfile" disabled={!allowFileSelection} /><br /><br />
        <button onClick={handleBrowse} disabled={allowFileSelection}>
          Browse
        </button>
        <input type="submit" disabled={!allowFileSelection} />
      </form>
    </div>
  );
}

export default Import;
