import React, { useState } from 'react';

function Import() {
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file && file.name.endsWith('.csv')) {
      const confirmed = window.confirm('Are you sure you want to upload a .csv file?');
      if (confirmed) {
        // Proceed with file upload
        console.log('Uploading .csv file:', file);
      } else {
        // User canceled file upload
        console.log('File upload canceled.');
      }
    }
  };

  return (
    <div>
      <h3>Show a file-select field which allows only one file to be chosen:</h3>
      <form>
        <label htmlFor="myfile">Select a file:</label>
        <input type="file" id="myfile" name="myfile" onChange={handleFileSelect} />
        <br />
        <br />
        <input type="submit" />
      </form>
    </div>
  );
}

export default Import;
