import React, { useState } from 'react';
import { Link } from 'react-router-dom';


function Scrolling() {

    const [visible, setVisible] = useState(true);

    const removeElement = () => {
        setVisible((prev) => !prev);
    };


  return (
    <>
    <div class="speech top">
    <text>New Features: Selecting a suggested recipe will auto-fill your Cart with respective ingredients. Turn on feature? </text>
    <div>
    {visible && (
    <button onClick={removeElement}  type="button" >Yes</button>
    )}
    </div>
    
    </div>
    <div class="scrollmenu">
        <a href="#home">Pizza</a>
        <a href="#news">Pasta</a>
        <a href="#contact">Lasagna</a>
        <a href="#about">Spigetti</a>
        <a href="#home">Pizza</a>
