        <a href="#about">Spigetti</a>
        <a href="#home">Pizza</a>
        <a href="#news">Pasta</a>
        <a href="#contact">Lasagna</a>
        <a href="#about">Spigetti</a>
    </div>
    </>
  );
}

export default Scrolling;
