import React, { useState } from "react"
import './App.css';
import {Login} from './pages/login_register/Login';
import {Register} from './pages/login_register/Register';
import {ShoppingList} from './pages/app/ShoppingList';
import axios from 'axios';

function App() {

  const [loginRegisterForm, setLoginRegisterForm] = useState('loginPage')
  const [loggedIn, setLoggedIn] = useState(false);

  const toggleForm = (formName) => {
    setLoginRegisterForm(formName);
  }

  const handleLogin = async (credentials) => {
    try {
      // Call your login API function with the provided credentials
      const response = await apiLogin(credentials);
      // Assuming the server responds with a success status
      if (response.status === 'success') {
        setLoggedIn(true); // Update the logged-in state
      } else {
        // Handle unsuccessful login, e.g., display an error message
      }
    } catch (error) {
      // Handle API call errors
    }
  }; 

  const handleRegister = async (userData) => {
    try {
      // Call your register API function with the provided user data
      const response = await apiRegister(userData);
      // Assuming the server responds with a success status
      if (response.status === 'success') {
        setLoggedIn(true); // Update the logged-in state
      } else {
        // Handle unsuccessful registration, e.g., display an error message
      }
    } catch (error) {
      // Handle API call errors
    }
  };
  if (loggedIn) {
    return (
      <div className="App">
        <ShoppingList/>
      </div>
    );
  }

  return (
    <div className="App">
      {
        loginRegisterForm === 'loginPage'? <Login onFormChange={toggleForm} /> : <Register onFormChange={toggleForm} />
      }
    </div>
  );
}

export default App;
