import React, { useState } from 'react';
import ShoppingRow from './ShoppingRow';
import { useNavigate } from 'react-router-dom';
import { SelectDrop } from './Select';

function ShoppingTable({ items }) {
  const [item, setItem] = useState('');
  const [quantity, setQuantity] = useState('');

  const navigate = useNavigate();
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleChange = (selectedOptions) => {
    setSelectedOptions(selectedOptions);
  };

  const handleDelete = (item) => {
    // Filter out the deleted item based on its id
    const updatedItems = items.filter((i) => i.id !== item.id);
    setItems(updatedItems);
};

  const options = [
    { value: 'option1', label: 'Apple' },
    { value: 'option2', label: 'Apricot' },
    { value: 'option3', label: 'etc' },
    // Add more options as needed
  ];

  const addItem = async () => {
    console.log(item);
    navigate('/shopping');
  };

  return (
    <div>
      <table className="items-table">
        <thead>
          <tr>
            <th>Item</th>
            <th>Quantity</th>
            <th>Add</th>
          </tr>
        </thead>
        <tbody>
        {items.map((item) => (
                    <ShoppingRow key={item.id} item={item} onDelete={handleDelete} />
                ))}
          <tr>
            <td>
              <SelectDrop options={options} onChange={handleChange} />
            </td>
            <td>
              <input
                type="number"
                value={quantity}
                placeholder="Enter quantity here"
                onChange={(e) => setQuantity(e.target.value)}
              />
            </td>
            <td>
              <button onClick={addItem}>Add</button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default ShoppingTable;
