import React, { useState } from 'react';

function Scrolling() {
    const [visible, setVisible] = useState(true);
    const [textContainerVisible, setTextContainerVisible] = useState(true);
    const [demoVisible, setDemoVisible] = useState(false);

    const removeElement = () => {
        setVisible((prevVisible) => !prevVisible);
        setTextContainerVisible((prevTextContainerVisible) => !prevTextContainerVisible);
        setDemoVisible(true);
    };

    return (
        <>
            {visible && (
                <div className="speech top">
                    {textContainerVisible && (
                        <div className="text-container">
                            <text>New Features: Selecting a suggested recipe will auto-fill your Cart with respective ingredients. Turn on feature? </text>
                        </div>
                    )}
                    <div className="button-container">
                        <button onClick={removeElement} type="button">
                            Yes
                        </button>
                        <button onClick={() => setVisible(false)} type="button">
                            No
                        </button>
                    </div>
                </div>
            )}

            {demoVisible && (
                <div className="demo-container">
                    <text>This is a demo text.</text>
                </div>
            )}
        </>
    );
}

export default Scrolling;
