import React, { useState } from "react"
import './App.css';
import { Routes, Route } from "react-router-dom"
import {Login} from './pages/login_register/Login';
import {Register} from './pages/login_register/Register';
import {Home} from './pages/app/Home';
import {PantryPage} from './pages/app/PantryPage';
import {AdvancedPage} from './pages/app/AdvancedPage';
import { Link } from "react-router-dom";
import { Sidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import axios from 'axios';

function App() {

  const [loginRegisterForm, setLoginRegisterForm] = useState('loginPage')
  const [loggedIn, setLoggedIn] = useState(false);

  const toggleForm = (formName) => {
    setLoginRegisterForm(formName);
  }

  const handleLogin = async (emailuser, password) => {
    try {
      if (emailuser === 'nish') {
        setLoggedIn(true);
      } else {
        setLoggedIn(false);
      }
    } catch (error) {
    }
  };

  /*const handleLogin = async (emailuser, password) => {
    try {
      // Call your login API function with the provided credentials
      const response = await apiLogin(credentials);
      // Assuming the server responds with a success status
      if (response.status === 'success') {
        setLoggedIn(true); // Update the logged-in state
      } else {
        // Handle unsuccessful login, e.g., display an error message
      }
    } catch (error) {
      // Handle API call errors
    }
  };
  */

  if (loggedIn) {
    return (
      <div className="App">
        <Routes>
        <Route path="/" element={ <Home/> } />
        <Route path="PantryPage" element={ <PantryPage/> } />
        <Route path="AdvancedPage" element={ <AdvancedPage/> } />
      </Routes>

      <Sidebar>
        <Menu
          menuItemStyles={{
            button: {
              // the active class will be added automatically by react router
              // so we can use it to style the active menu item
              [`&.active`]: {
                backgroundColor: '#13395e',
                color: '#b6c8d9',
              },
            },
          }}
        >
          <MenuItem component={<Link to="/" />}> Shopping List</MenuItem>
          <MenuItem component={<Link to="/PantryPage" />}> Pantry</MenuItem>
          <MenuItem component={<Link to="/AdvancedPage" />}> Advanced Settings</MenuItem>
        </Menu>
      </Sidebar>
    </div>
    );
  }

  return (
    <div className="App">
      {
        loginRegisterForm === 'loginPage'? <Login onFormChange={toggleForm} onLogin={handleLogin}/> : <Register onFormChange={toggleForm} />
      }
    </div>
  );
}

export default App;
