import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Scrolling() {
  const [visible, setVisible] = useState(true);
  const [demoVisible, setDemoVisible] = useState(true);

  const removeElement = (isTrue) => {
    setVisible((prev) => !prev);
    if (isTrue) {
      setDemoVisible((prev) => !prev);
    }
  };

  return (
    <>
      {visible && (
        <div className="speech top" onClick={() => removeElement(true)}>
          <div className="text-container">
            <text>New Features: Selecting a suggested recipe will auto-fill your Cart with respective ingredients. Turn on feature? </text>
          </div>
          <div>
            <div className="button-container">
              <button type="button" value={true}>
                Yes
              </button>
              <button type="button" value={false}>
                No
              </button>
            </div>
          </div>
        </div>
      )}

      {demoVisible && (
        <div className="speech top" onClick={() => removeElement(false)}>
          <div className="text-container">
            <text>Demo....</text>
          </div>
        </div>
      )}

      <div className="scrollmenu">
        <a href="#home">Pizza</a>
        <a href="#news">Pasta</a>
        <a href="#contact">Lasagna</a>
        <a href="#about">Spaghetti</a>
        <a href="#home">Linguini</a>
        <a href="#news">Tomato Chutney</a>
        <a href="#contact">Chicken Curry</a>
        <a href="#about">Meatballs w/ Marinara</a>
        <a href="#home">Chicken Tikka</a>
        <a href="#news">Chicken Tandoori</a>
        <a href="#contact">Eggplant Alfredo</a>
        <a href="#about">Breadsticks w/ sauce</a>
      </div>
    </>
  );
}

export default Scrolling;
